function goVerified(nid) {
	jQuery.getJSON('/backend/goVerified/'+nid,{},function (result) {
		jQuery(".approved_"+nid).show();
		jQuery(".approved_hand_"+nid).hide();
		jQuery("#online_"+nid).removeClass("offline").removeClass("moderate").removeClass("validate").addClass("online");
		jQuery("#spanStato_"+nid).html("Online");
		jQuery("#goOff_"+nid).show();
		jQuery("#goOn_"+nid).hide();
		jQuery("#onlineD_"+nid).hide();
	});
	
}

function goOnline(nid) {
	jQuery.getJSON('/backend/goOnline/'+nid,{},function (result) {
		jQuery("#online_"+nid).removeClass("offline").removeClass("moderate").removeClass("validate").removeClass("duplicate").addClass("online");
		jQuery("#spanStato_"+nid).html("Online");
		jQuery("#goOff_"+nid).show();
		jQuery("#goOn_"+nid).hide();
		jQuery(".approved_"+nid).show();
		jQuery(".approved_hand_"+nid).hide();
		jQuery("#onlineD_"+nid).hide();

	});
	
}

function goOffline(nid) {
	jQuery.getJSON('/backend/goOffline/'+nid,{},function (result) {
		jQuery("#online_"+nid).removeClass("online").addClass("offline");
		jQuery("#goOff_"+nid).hide();
		jQuery("#goOn_"+nid).show();
		jQuery("#onlineD_"+nid).hide();
	});
	
}

function goDelete(nid) {
	var answer = confirm(Drupal.t("Sicuro di voler cancellare l'annuncio ? L'operazione non puo' essere annullata."));
	if (answer) {
		jQuery.getJSON('/backend/deleteAnnounce/'+nid,{},function (result) {
			jQuery('#annuncio_'+nid).css('opacity',0.4);	
			jQuery('#annuncio_'+nid+"_l2").css('opacity',0.4);	
		});
	}	
}

function goDeleteNoConfirmation(nid) {

                jQuery.getJSON('/backend/deleteAnnounce/'+nid,{},function (result) {
                        jQuery('#annuncio_'+nid).css('opacity',0.4);
                        jQuery('#annuncio_'+nid+"_l2").css('opacity',0.4);
                });
}


function goRefuse(nid) {
	var pos = jQuery('#annuncio_'+nid).position();
	var h = jQuery('#annuncio_'+nid).height();
	var l = pos.left+50;
	var t = pos.top + (h/2) - 100;
	
	jQuery('#rifiuta_'+nid).css('position','absolute').css('left',l+"px").css('top',t+"px").css('z-Index',10).show();
	jQuery('#submit_'+nid).click(function(e) {
		e.preventDefault();
		jQuery.post('/backend/goRefuse',{'nid': nid,'msg' : jQuery('#msg_'+nid).val(), 'uid': jQuery('#uid_'+nid).val()},function (result) {
			if (result.status < 0) {
				jQuery('#annuncio_'+nid).css('opacity',1);
				alert(result.msg);
				return;				
			}

			jQuery('#annuncio_'+nid+"_l2").css('opacity',0.4);
			jQuery('#annuncio_'+nid).css('opacity',0.4);	
			jQuery("#online_"+nid).removeClass("online").addClass("offline");
			jQuery("#goOff_"+nid).hide();
			jQuery("#goOn_"+nid).show();
			jQuery('#rifiuta_'+nid).hide();
			jQuery('#rifiutaLink_'+nid).hide();
			jQuery("#onlineD_"+nid).hide();
		},'json');
		return false;
		
	});
}

function deleteImg(nid,nrImg) {
	jQuery.getJSON('/backend/deleteNodeImg',{'nid':nid, 'nrImg':nrImg},function (result) {
	
		//Cambio il colore per indicare che l'operazione  stata portata a buon fine
		if (result.status < 0) alert(result.msg);
		else {
		    jQuery('#img_'+nid+"_"+nrImg).hide();
		    jQuery('#annuncio_'+nid).css('opacity',0.4);
		}
	});
}

function OpenWin(strLink){
         window.open(strLink,'action','toolbar=no,width=500,height=300,scrollbars=no,status=no');
}

function updatePhrase(id) {
	jQuery('#edit-id').attr('value',id);
	jQuery('#edit-op').attr('value','u');
}

function removePhrase(id) {
	jQuery('#edit-id').attr('value',id);
	jQuery('#edit-op').attr('value','d');
}

function cambiaStatoModerazione(tid) {
	jQuery.getJSON("/backend/amministrazione/cambiaStatoModerazioneMC/"+tid,{},function (result) {
		alert(result.msg);
		if (result.status == 0) {
			if (result.code == 0) {
				jQuery(".pre_"+tid).show();
				jQuery(".post_"+tid).hide();
			}
			else {
				jQuery(".pre_"+tid).hide();
				jQuery(".post_"+tid).show();
			}
		}
	});
}

function backend_removeOptions(id) {
  var elSel = document.getElementById(id);
  if (elSel == null) return;
  while (elSel.length > 0) {
    elSel.remove(elSel.length - 1);
  }
}

